import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const Home = () => {
  const [users, setUsers] = useState([]);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    axios.get('https://jsonplaceholder.typicode.com/users')
      .then((response) => {
        // Modify user data to reflect Indian names, emails, and phone numbers
        const indianUsers = response.data.map(user => ({
          ...user,
          name: `Shri ${user.name.split(' ')[0]} Sharma`, // Indian-style names
          email: `${user.name.split(' ')[0].toLowerCase()}@example.in`, // Indian-style email format
          phone: `+91 ${Math.floor(9000000000 + Math.random() * 1000000000)}` // Random Indian mobile number
        }));
        setUsers(indianUsers);
        setLoading(false);
      })
      .catch(() => {
        setError('Failed to fetch users.');
        setLoading(false);
      });
  }, []);

  // Function to handle deletion of a user
  const handleDelete = (id) => {
    if (window.confirm("Are you sure you want to delete this user?")) {
      axios.delete(`https://jsonplaceholder.typicode.com/users/${id}`)
        .then(() => {
          setUsers(users.filter(user => user.id !== id));
        })
        .catch(() => {
          setError('Failed to delete user.');
        });
    }
  };

  if (loading) {
    return <p className="text-center text-gray-600">Loading...</p>;
  }

  return (
    <div className="container mx-auto py-8 px-4 md:px-0">
      <h1 className="text-4xl font-semibold text-center mb-6 text-gray-800">User Management</h1>
      {error && <p className="text-red-500 text-center">{error}</p>}
      
      <div className="overflow-x-auto shadow-md sm:rounded-lg">
        <table className="min-w-full bg-white border border-gray-300">
          <thead className="bg-gray-800 text-white">
            <tr>
              <th className="py-3 px-4 text-left">Name</th>
              <th className="py-3 px-4 text-left">Email</th>
              <th className="py-3 px-4 text-left">Phone</th>
              <th className="py-3 px-4 text-left">Actions</th>
            </tr>
          </thead>
          <tbody>
            {users.map(user => (
              <tr key={user.id} className="border-b border-gray-200 hover:bg-gray-100">
                <td className="py-3 px-4 text-gray-700">{user.name}</td>
                <td className="py-3 px-4 text-gray-700">{user.email}</td>
                <td className="py-3 px-4 text-gray-700">{user.phone}</td>
                <td className="py-3 px-4">
                  <Link to={`/user/${user.id}`} className="text-blue-600 hover:underline">View</Link>
                  <Link to={`/edit/${user.id}`} className="ml-4 text-green-600 hover:underline">Edit</Link>
                  <button
                    className="ml-4 text-red-600 hover:underline"
                    onClick={() => handleDelete(user.id)}
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Home;
